mapper.select:
select user_id userId, login_name loginName, password, name, last_login lastLogin, ip, status, skin, email, phone from sys_user where 1=1
and ( (login_name = #{loginName} and password = #{password}) or (email = #{loginName} and password = #{password}) ) and status != -1
and user_id = #{userId}

mapper.update:
update sys_user set 
last_login=#{lastLogin} 
where user_id=#{userId}

mapper.select:
select u.user_id userId, u.login_name loginName, u.password, u.name, u.last_login lastLogin, u.ip, u.status, u.description, u.email, u.phone, u.skin from sys_user u where u.status != -1
and CONCAT(IFNULL(u.login_name,''),IFNULL(u.name,'')) like #{keyword}
limit #{from},#{size}

